func:
let NMAP_WAIT = 2
let FAST_CNT = 30
let FAST_DELAY = 1
let SLOW_DELAY = 10
let wait = 0

if ((msg.down_cnt === undefined) || (msg.down_cnt > 100000))
  msg.down_cnt = 0;
msg.down_cnt++;
msg.payload = "off"
if (msg.down_cnt < FAST_CNT)
    msg.delay = FAST_DELAY * 1000
else
    msg.delay = SLOW_DELAY * 1000
if (msg.down_cnt > FAST_CNT) {
    wait = ((FAST_CNT - 1) * FAST_DELAY) + (FAST_CNT * NMAP_WAIT) + (msg.down_cnt - FAST_CNT) * (SLOW_DELAY + NMAP_WAIT)
} else {
    wait = ((msg.down_cnt - 1) * FAST_DELAY) + (msg.down_cnt * NMAP_WAIT)
}
msg.current_wait = wait
return msg;

out_msg = {};
out_msg.name = msg.name;
out_msg.mode = msg.mode;

if ((msg.mode == msg.data.event.new_state.state) &&
    (msg.temp == msg.data.event.new_state.attributes.temperature) &&
    ((msg.fan_mode == msg.data.event.new_state.attributes.fan_mode) ||
     ((msg.fan_mode == 0) && (msg.data.event.new_state.attributes.fan_mode == "auto"))) &&
    (msg.v_mode == msg.data.event.new_state.attributes.swing_mode) &&
    (msg.h_mode == msg.data.event.new_state.attributes.vane_horizontal))
  out_msg.payload = true;
else
  out_msg.payload = false;

return out_msg;

if (msg.topic.indexOf("input_number.hvac_") === -1) {
    name = msg.topic.replace("climate.hvac_", "");
    timer_val = null;
    mode = 'state';
} else {
    name = msg.topic.replace("input_number.hvac_", "");
    name = name.replace("_timer", "");
    timer_val = parseFloat(msg.payload);
    mode = 'timer';
}
return { name, mode, timer_val };

context.data = context.data || {}
if (msg.data.entity_id == "input_boolean.gloria_connected") {
    if (msg.data.state == "on") context.data.gloria_home = true
    else                        context.data.gloria_home = false
}
if (msg.data.entity_id == "input_boolean.salvatore_connected") {
    if (msg.data.state == "on") context.data.salvatore_home = true
    else                        context.data.salvatore_home = false
}
if (context.data.gloria_home != null && context.data.salvatore_home != null)
{
    if (context.data.gloria_home && context.data.salvatore_home)
        msg = { "payload": "Bentornati a casa" }
    else if (context.data.gloria_home)
        msg = { "payload": "Bentornata a casa, Gloria" }
    else if (context.data.salvatore_home)
        msg = { "payload": "Bentornato a casa, Salvatore" }
    context.data = null
} else msg = null
return msg
